$(document).on("click",".minus-btn, .plus-btn",function(){const o=$(this).data("id"),s=$(this).data("url"),c=$(this).data("action"),t=$(this).siblings(".quantity");let e=parseInt(t.text());const u=document.querySelector("#csrf-token").value;c==="minus"&&e>1?e--:c==="plus"&&e++,$.ajax({type:"POST",url:s,data:{id:o,quantity:e,_token:u},success:function(n){$("#cart-count").text(n.count),$("#cart-total").text(n.total),t.text(n.quantity)},error:function(n,r,a){alert(n.responseText)}})});$(document).on("click",".delete-btn",function(){const o=$(this).data("id"),s=$(this).data("url"),c=document.querySelector("#csrf-token").value;$.ajax({type:"POST",url:s,data:{id:o,_token:c},success:function(t){$("#cart-count").text(t.count),$("#cart-total").text(t.total);const e="#cart-item-"+o;$(e).fadeOut(300,function(){$(this).remove()})},error:function(t,e,u){alert(t.responseText)}})});
